import React, { Component } from 'react';
import './App.css';
import SearchField from './SearchField';
import Countries from './Countries';

class App extends Component {
	constructor() {
		super();
		this.state = {
			countries: [],
			searchfield: ''
		}
	}
	componentDidMount = () => {
		fetch('https://restcountries.com/v3.1/all').then(res => res.json()).then(country => this.setState({countries: country}));
	}
	onSearch = (event) => {
		this.setState({ searchfield: event.target.value });
		console.log(this.state.searchfield);
	}
	render() {
		//{name, tld, cca2, ccn3, cca3, cioc, independent, status, unMember, currencies, idd, capital, altSpellings, region, subregion, languages, translations, latlng, landlocked, borders, area, demonyms, flag, maps, population, gini, fifa, car, timezones, continents, flags, coatOfArms, startOfWeek, capitalInfo, postalCode}
		const { countries, searchfield } = this.state;
		const countryFilter = countries.filter(cou => {
			return cou.name.common.toLowerCase().includes(searchfield.toLowerCase())
		})
	  return (
	    <div>
	      <div className="logoText">Country List</div>
	      <div className="searchBlock">
	      	<div className="overlay">
		      	<div className="searchLabel">Find Countries by Name</div>
		      	<div><SearchField searchResult={this.onSearch} /></div>
	      	</div>
	      </div>
	      <div className="country_card_block">
	      	{
	      		(!countries.length) ?
	      		<div>loading...</div> : 
	      		(<>
							{console.log(countries[0].currencies.name, countries[0].currencies.symbol, countries[0].currencies[1], countries[20].languages, countries[20].timezones)}
		      		<Countries country={countryFilter}/>
		      	</>
		      	)
	      	}
	      </div>
	    </div>
	  );
	}
}

export default App;
