import React, { Component } from 'react';
import './App.css';
import SearchField from './SearchField';
import CountryCard from './CountryCard';

class App extends Component {
	constructor() {
		super();
		this.state = {
			country: [],
			searchfield: ''
		}
	}
	componentDidMount = () => {
		fetch('https://restcountries.com/v3.1/all').then(res => res.json()).then(countries => this.setState({country: countries}));
	}
	onSearch = (event) => {
		this.setState({ searchfield: event.target.value });
		console.log(this.state.searchfield);
	}
	render() {
		const { country, searchfield } = this.state;
	  return (
	    <div>
	      <div className="logoText">Country List</div>
	      <div className="searchBlock">
	      	<div className="overlay">
		      	<div className="searchLabel">Find Countries by Name</div>
		      	<div><SearchField searchResult={this.onSearch} /></div>
	      	</div>
	      </div>
	      <div className="country_card_block">
	      	{
	      		(!country.length) ?
	      		<div>loading</div> : 
	      		(<>
						console.log(country[0])
	      		<CountryCard name="Japan"/>
		      	<CountryCard name="United States of America"/>
		      	<CountryCard name="Republic of Ecuador"/>
		      	<CountryCard name="Republic of Iceland"/>
		      	<CountryCard name="Republic of Kazakhstan"/>
		      	<CountryCard name="Palestine"/>
		      	<CountryCard name="Swaziland"/>
		      	</>
		      	)
	      	}

	      </div>
	    </div>
	  );
	}
}

export default App;
